new Vue({el:"#index",data:{show:true,more:false,hotlist:[],listHead:[],list:[],detail:[],textR:"",id:""},mounted:function(){let that=this;$.ajax({url:"/web_api/notice/notice.php?m=hotlist",type:"get",async:false,dataType:"json",success:function(a){that.hotlist=a.data}});$.ajax({url:"/web_api/notice/notice.php?m=headlist",type:"get",async:false,dataType:"json",success:function(a){that.listHead=a.data;that.id=that.listHead[0].id;that.lists(that.id)}});if(GetQueryString("id")!=null){that.mess(GetQueryString("id"))}},methods:{lists:function(a){let that=this;that.show=true;that.more=false;that.id=a;$.ajax({url:"/web_api/notice/notice.php?m=articellist&id="+a,type:"get",async:false,dataType:"json",success:function(b){that.list=b.data}})},mess:function(a){let that=this;that.show=false;$.ajax({url:"/web_api/notice/notice.php?m=messageprt&id="+a,type:"get",async:false,dataType:"json",success:function(b){that.detail=b.data[0]}})},returnMess:function(){this.show=true},research:function(){let that=this;that.show=true;$.ajax({url:"/web_api/notice/notice.php?m=articellist",type:"get",data:{id:that.id,title:that.textR},async:false,dataType:"json",success:function(a){if(a.data.length==0){layer.msg("没有搜索到相关信息！");return false}that.list=a.data}})}}});